import React from "react"
import "./button.css"

class Selector extends React.Component
{


    onDragMouse()
    {

       let timer = setInterval(() => {


        if (this.state.direction === 1 && this.state.position >= 200){
            this.setState(
                {
                    direction: -1
                }
            )
        }
        else if (this.state.direction === -1 && this.state.position <= -200){
            this.setState(
                {
                    direction: 1
                }
            )
        }

            this.setState({
                position : this.state.position + this.state.direction
                }
            )
        }, 10);
    }

    constructor(props)
    {
        super(props)

        this.state ={
            position : props.position,
            direction: 1
        }

        this.onDragMouse = this.onDragMouse.bind(this);
    }


    render()
    {
        return(
        <div className = "selecting" onClick = {this.onDragMouse} style = {{marginLeft : this.state.position + "px" || this.props.position + "px" }}> </div>
        );
    }


}

export default Selector;
