import React from "react"
import "./button.css"

class Selector extends React.Component
{


    onDragMouse()
    {

       let timer = setInterval(() => {

        let modiferValue = 1;

        if (this.state.direction === "positive" && this.state.position >= 600){
            modiferValue = -1;
            this.setState(
                {
                    direction: "negative"
                }
            )
        }
        else if (this.state.direction === "negative" && this.state.position >= 450){
            modiferValue = 1;

            this.setState(
                {
                    direction: "positive"
                }
            )
        }

            this.setState({
                position : this.state.position + modiferValue
                }
            )
        }, 25);

       setTimeout(()=> {clearInterval(timer);}, 10000)
    }

    constructor(props)
    {
        super(props)

        this.state ={
            position : props.position,
            direction: "positive"
        }

        this.onDragMouse = this.onDragMouse.bind(this);
    }


    render()
    {
        return(
        <div className = "selecting" onClick = {this.onDragMouse} style = {{marginLeft: this.state.position + "px"}}> </div>
        );
    }


}

export default Selector;
