import React from "react"
import "./button.css"
import Tone from "tone"
import SquareButton from "./SquareButton"
class Selector extends React.Component
{

    changeMusic()
    {
        let synth = new Tone.PolySynth(6, Tone.Synth).toMaster();
        let soundArray = ["C4", "D4", "E4", "F4", "G4", "A4", "B4", "C5"]
        let soundArray2 = ["E4", "F4", "G4", "A4", "B4", "C5", "D5", "E5"]
        let soundArray3 = ["G4", "A4", "B4", "C5", "D5", "E5", "F5", "G5"]

        let randomNumber = Math.floor(Math.random() * (soundArray.length));

        synth.triggerAttackRelease([soundArray[randomNumber], soundArray2[randomNumber] ,soundArray3[randomNumber]], "16n");


    }



    onDragMouse()
    {

       setInterval(() => {


        if (this.state.direction === 1 && this.state.position >= 400){
            this.setState(
                {
                    direction: -1
                }
            )
        }
        else if (this.state.direction === -1 && this.state.position <= -275){
            this.setState(
                {
                    direction: 1
                }
            )
        }

            this.setState({
                position : this.state.position + (this.state.direction * 1)
                }
            )

            if (this.state.position === -200)
            {
               this.changeMusic();
               this.setState(
                   {
                   squarePos : -200
                   }
               )
            }
            else if (this.state.position === -100)
            {
                this.changeMusic();
                this.setState(
                    {
                    squarePos : -100
                    }
                )
            }
            else if (this.state.position === 100)
            {
                this.changeMusic();

                this.setState(
                    {
                    squarePos : 100
                    }
                )
            }
            else if (this.state.position === 350)
            {
                this.changeMusic();

                this.setState(
                    {
                    squarePos : 350
                    }
                )
            }

        }

        , 5);
    }

    constructor(props)
    {
        super(props)

        this.state ={
            position : props.position,
            direction: 1
        }

        this.onDragMouse = this.onDragMouse.bind(this);
    }


    render()
    {
        return(
        <div id = "overAll">
        <div id = "contentA">
        <div id = "buttonBlock">
        <SquareButton color="goldenrod" position = {this.state.squarePos}></SquareButton>
        <SquareButton color="red" position = {this.state.squarePos}></SquareButton>
        <SquareButton color= "slategrey" position = {this.state.squarePos}></SquareButton>
        <SquareButton color = "darkcyan" position = {this.state.squarePos}></SquareButton>
        </div>
        </div>
        <div id = "contentB">
        <div id = "selectorblock">
        <div className = "selecting" onClick = {this.onDragMouse} style = {{marginLeft : this.state.position + "px" || this.props.position + "px" }}> </div>
        </div>
        </div>
        </div>
        );
    }


}

export default Selector;
