import React from "react"
import "./button.css"

class SquareButton extends React.Component
{

    constructor(props){
    super(props);

    this.onButtonPress = this.onButtonPress.bind(this);

    this.state = {

    }


}


    onButtonPress(){

        let colorArray = ["red", "darkcyan", "goldenrod", "slategrey", "rebeccapurple", "plum", "sandybrown", "pink"]
        let randomNumber = Math.floor(Math.random() * (colorArray.length));

        while (colorArray[randomNumber] === (this.state.color || this.props.color))
        {
           randomNumber = Math.floor(Math.random() * (colorArray.length));    
        }


        this.setState(
            {
                color: colorArray[randomNumber]
            }
        )


    }



    render(){
       return <span className="square" onClick = {this.onButtonPress} style = {{backgroundColor: this.state.color || this.props.color}}></span>
    }




}

export default SquareButton
