import React from "react"
import "./button.css"

class SquareButton extends React.Component
{

    constructor(props)
    {
    super(props);

    this.state = 
    {
        buttonPressed: "false",
        color: "goldenrod"
    }

    this.onButtonPress = this.onButtonPress.bind(this);
    this.handleChange = this.handleChange.bind(this);

}


    onButtonPress()
    {
        this.setState(
            {
                buttonPressed: "true",
                color: "red"
            }
        )

        console.log("Hello")





    }

    handleChange(event)
    {
        this.setState(
            {
                color: event.target.value
            }
        )
    }


    render()
    {
       return <div className="square" onClick = {this.onButtonPress} style = {{backgroundColor: this.color}}></div>
    }




}

export default SquareButton
