import React from "react"
import "./button.css"

class Selector extends React.Component
{

    movePos(newPos)
    {
        let newPosition = newPos + 1;
        this.setState(
            {
            position : newPosition
            }
        )
        console.log(this.state.position);
    }


    onDragMouse()
    {
        setInterval(this.movePos(this.state.position), 3000);

    }

    constructor(props)
    {
        super(props)

        this.state ={
            position : props.position
        }

        this.onDragMouse = this.onDragMouse.bind(this);
        this.movePos = this.movePos.bind(this);
    }


    render()
    {
        return(
        <div className = "selecting" onClick = {this.onDragMouse} style = {{marginLeft: this.state.position + "px"}}> </div>
        );
    }


}

export default Selector;
